
/*
 *  用户模型.
 */

var mongodb = require('./db');
function User(user) {
	this.name = user.name;
	this.password = user.password;
};
module.exports = User;
User.prototype.save = function save(callback) {
// 存入 Mongodb 的文档
	var user = {
		name: this.name,
		password: this.password
	};
	mongodb.open(function(err, db) {
		if (err) {
			return callback(err);
		}
	// 读取 users 集合
		db.collection('users', function(err, collection) {
			if (err) {
				mongodb.close();
				return callback(err);
			}
			//为 name 属性添加索引
			/* 	can not use a writeConcern without a provided callback
			谷歌后，找到了：将collection.ensureIndex('name',{unique: true});改成：
			collection.ensureIndex('name',{unique: true},{w: 0});这种该法是你不考虑跟数据库处理的结果，
			如果担心出错，你可以加一个回调函数。 */
			collection.ensureIndex('name', {unique: true}, {w: 0});
			// 写入 user 文档
			collection.insert(user, {safe: true}, function(err, user) {
				mongodb.close();
				callback(err, user);
			});
		});
	});
};
User.get = function get(username, callback) {
	mongodb.open(function(err, db) {
		if (err) {
			return callback(err);
		}
		// 读取 users 集合
		db.collection('users', function(err, collection) {
			if (err) {
				mongodb.close();
				return callback(err);
			}
			// 查找 name 属性为 username 的文档
			collection.findOne({name: username}, function(err, doc) {
				mongodb.close();
				if (doc) {
					// 封装文档为 User 对象
					var user = new User(doc);
					callback(err, user);
				} else {
					callback(err, null);
				}
			});
		});
	});
};